import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Home = () => {

    let history = useHistory()

    const [channelName, setChannelName] = useState([]);

    useEffect(async () => {

        const userID = localStorage.getItem("chatbox-user")

        const response = await axios.get('http://localhost:8010/channels/userChannels/' + userID)

        console.log("home response: ", response.data);

        setChannelName(response.data)


    }, []);

    const enterChat = (e) => {

        localStorage.removeItem("channel")
        localStorage.setItem("channel", e)

        const ok = localStorage.getItem("channel")

        console.log("enterchat okk", ok);

    }

    const logout = () => {

        history.push("/login")
        localStorage.clear()
    }

    return (
        <div>
            {console.log("home state channelName: ", channelName)}

            <div className="container">
                <div className="row">
                    <div className="col-2 offset-6">
                        <button type="button" class="btn btn-primary">New Channel</button>
                    </div>
                    <div className="col-2 offset-1">
                        <button type="button" class="btn btn-danger" onClick={logout}>Logout</button>
                    </div>
                </div>
                <div>
                    {channelName.map((elem) =>

                        <div class="list-group">
                            {/* <button  type="button" class="list-group-item list-group-item-action" onClick={()=>enterChat(elem._id)} >{elem.name}</button> */}
                            <a href="/chat" class="list-group-item list-group-item-action" onClick={()=>enterChat(elem._id)}>{elem.name}</a>

                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Home;
