import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import Modal from "react-modal"
import "../styles/homeStyle.css";

const Home = () => {

    let history = useHistory()

    const [channelName, setChannelName] = useState([]);
    const [modalIsOpen, setIsOpen] = useState(false);
    const [chName, setChName] = useState('');
    const [chOwner, setChOwner] = useState([]);
    const [chUsers, setChUsers] = useState([]);


    useEffect(async () => {

        const userID = localStorage.getItem("chatbox-user")

        const response = await axios.get('http://localhost:8010/channels/userChannels/' + userID)

        console.log("home response: ", response.data);

        setChannelName(response.data)


    }, []);

    const enterChat = (e) => {

        localStorage.removeItem("channel")
        localStorage.setItem("channel", e)

        const ok = localStorage.getItem("channel")

        console.log("enterchat okk", ok);

    }

    const customStyles = {
        content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
        },
    };

    const newChannel = () => {

        setIsOpen(true)

        const newChannelInfo = {
            name: chName,
            owner: [chOwner],
            users: [chUsers]
        }

        console.log("oooo: ",newChannelInfo);

    }

    const saveChannel = () => {
        setIsOpen(false)
    }

    const logout = () => {

        history.push("/login")
        localStorage.removeItem("chatbox-user")
    }

    return (
        <div>
            {console.log("home state channelName: ", channelName)}

            <div className="container" id="big-container">
                <div className="row">
                    <div className="col-2 ">
                        <button type="button" class="btn btn-primary" onClick={newChannel}>New Channel</button>
                        <Modal isOpen={modalIsOpen} style={customStyles}>
                            <div class="input-group mb-3">
                                <input type="text" class="form-control" placeholder="Name" onChange={(e) => setChName(e.target.value)} />
                            </div>
                            <div class="input-group mb-3">
                                <input type="number" class="form-control" placeholder="your number" onChange={(e) => setChOwner(e.target.value)} />
                            </div>
                            <div class="input-group mb-3">
                                <input type="number" class="form-control" placeholder="add a friend number"/>
                                <button class="btn btn-outline-secondary" type="button" onClick={(e) => setChUsers(e.target.value)} >Add</button>

                            </div>
                            <button onClick={saveChannel}>confirm</button>


                        </Modal>
                    </div>
                    <div className="col-2 offset-1">
                        <button type="button" class="btn btn-danger" onClick={logout}>Logout</button>
                    </div>
                </div>
                <div id="channelList">
                    {channelName.map((elem) =>

                        <div class="list-group">
                            {/* <button  type="button" class="list-group-item list-group-item-action" onClick={()=>enterChat(elem._id)} >{elem.name}</button> */}
                            <a href="/chat" class="list-group-item list-group-item-action" onClick={() => enterChat(elem._id)}>{elem.name}</a>

                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Home;
