import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import "../styles/signupStyle.css"

const Signup = () => {

    let hystory = useHistory()

    const [email, setEmail] = useState("");
    const [telNumber, setTelNumber] = useState(0);
    const [lastName, setLastname] = useState("");
    const [firstName, setFirstName] = useState("");
    const [userState, setUserState] = useState("");

    const [password, setPassword] = useState("");
    const [confPsw, setConfPsw] = useState("");
    const [dateOfB, setDateOB] = useState("");

    // useEffect(async () => {




    // }, [confPsw]);

    const signupPost = async (req, res) => {

        if (password === confPsw) {

            const userData = {

                firstName,
                lastName,
                birthday: dateOfB,
                state: userState,
                telNumber,
                email,
                password,
                confPsw

            }

            console.log("userdata", userData);

            const userDataArray = Object.values(userData)

            const userDataArrayIndex = userDataArray.indexOf("")

            const userdataKeys = Object.keys(userData)

            const inputUnfilled = userdataKeys[userDataArrayIndex]

            for (let i = 0; i <= userDataArray.length; i++) {

                if (userDataArray[i] === "") {

                    return alert(`${inputUnfilled} unfilled`)

                }
            }

            // userDataValues.map((elem) => elem === "" ? console.log(" okkkkk"):console.log("noooooo"))

            console.log("userDataValues :", userDataArray);
            console.log("inputUnfilled :", inputUnfilled);


            // Event.preventDefault()

            const response = await axios.post("http://localhost:8010/users/addUser", userData)

            if (response.status !== 200) {
                alert("signup failed")
            }else if (response.status === 200){

                alert('User created, you can connect now !')
                hystory.push('/login')

            }

            console.log("response :", response);

        } else {
            alert("password incorrect")
        }
        console.log(email, dateOfB);
    }

    // const validInput = () => {

    //     const userData = {

    //         firstName,
    //         lastName,
    //         birthday: dateOfB,
    //         state: userState,
    //         telNumber,
    //         email,
    //         password,
    //         confPsw

    //     }

    //     userData.map(elem => elem.length == 0)


    // }



    return (
        <div>
            <div className="container">
                <h1 className="text-center">Signup</h1>
                <div className="mb-2">
                    <label className="form-label">FirstName</label>
                    <input className="form-control" onChange={(e => setFirstName(e.target.value))} type="text" />
                </div>
                <div className="mb-2">
                    <label className="form-label">Lastname</label>
                    <input className="form-control" onChange={(e => setLastname(e.target.value))} type="text" />
                </div>
                <div className="mb-2">
                    <label className="form-label">State</label>
                    <input className="form-control" onChange={(e => setUserState(e.target.value))} type="text" />
                </div>
                <div className="mb-2">
                    <label className="form-label">Email</label>
                    <input className="form-control" onChange={(e => setEmail(e.target.value))} type="email" />
                </div>
                <div className="mb-2">
                    <label className="form-label">Tel.Number</label>
                    <input className="form-control" onChange={(e => setTelNumber(e.target.value))} type="number" />
                </div>
                <div className="mb-2">
                    <label className="form-label">Password</label>
                    <input className="form-control" onChange={(e => setPassword(e.target.value))} type="password" />
                    <p>* password lenght up to 8 character</p>
                </div>
                <div className="mb-2">
                    <label className="form-label">Confirm password</label>
                    <input className="form-control" onChange={(e => setConfPsw(e.target.value))} type="password" />
                </div>
                <div className="mb-2">
                    <label className="form-label">Date of birth</label>
                    <input className="form-control" onChange={(e => setDateOB(e.target.value))} type="date" />
                </div>
                <button type="button" className="btn btn-primary mt-2" onClick={signupPost}>Submit</button>
            </div>
        </div>
    );
}

export default Signup;
