import axios from 'axios';
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import '../styles/loginStyle.css';

const Login = () => {

    let history = useHistory()

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    // const loginData = {
    //     ema
    // }

    const logUser = async () => {

        const response = await axios.post('http://localhost:8010/users/login', {email, password})

        console.log("login response :", response);

        if(response.status === 200){

            console.log("response.status :", response.status);

            localStorage.setItem("chatbox-user", response.data.userID )

            history.push('/home')

        }else{
            alert("user not login")
        }
    }

    console.log(email);
    return (
        <div className="container" id="logBorder">
            <label class="form-label">Email</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control" placeholder="username@example.com" onChange={(e) => setEmail(e.target.value)} />
            </div>
            <label class="form-label">Password</label>
            <div class="input-group mb-3">
                <input type="password" class="form-control" onChange={(e) => setPassword(e.target.value)} />
            </div>
            <button type="button" class="btn btn-success" onClick={logUser} >Login</button>
        </div>
    );
}

export default Login;
